import React,{Component} from 'react';
import {Modal, Col, Row, Form, Button} from 'react-bootstrap';
import {FormControl, FormGroup, FormLabel} from 'react-bootstrap';



export class EditStudentModal extends Component{
    constructor(props){
        super(props);
        this.handleSubmit=this.handleSubmit.bind(this);
    }


    handleSubmit(event){
        event.preventDefault();
        fetch('http://127.0.0.1:8000/student/', {
            method:'PUT',
            headers:{
                'Accept':'application/json',
                'Content-Type':'application/json'
            },
            body:JSON.stringify({
                studentId:event.target.studentId.value,
                Firstname:event.target.Firstname.value,
                Lastname:event.target.Lastname.value,
                Email:event.target.Email.value,
                Course:event.target.Course.value
            })
        })
        .then(response=>response.json())
        .then((result)=>{
            alert(result);
        },
        (error)=>{
            alert("Failed");
        })
    }

    render(){
        return(
            <div className="container">
                
                <Modal
                    {...this.props}
                    size="lg"
                    aria-labelledby="contained-modal-title-vcenter"
                    centered >

                    <Modal.Header closeButton>
                        <Modal.Title id="contained-modal-title-vcenter">
                            Edit Student Information
                        </Modal.Title>
                    </Modal.Header>
                    <Modal.Body>
                        <Row>
                            <Col sm={6}>
                                <Form onSubmit={this.handleSubmit}>
                                <Form.Group controlId="studentId">
                                        <Form.Label>Student Id</Form.Label>
                                        <Form.Control type="text" name="studentId" disabled defaultValue={this.props.stuid} required placeholder="" />
                                </Form.Group>
                                    <Form.Group controlId="Firstname">
                                        <Form.Label>First Name</Form.Label>
                                        <Form.Control type="text" name="Firstname" defaultValue={this.props.F} required placeholder="" />
                                </Form.Group>

                                <Form.Group controlId="Lastname">
                                        <Form.Label>Last Name</Form.Label>
                                        <Form.Control type="text" name="Lastname" defaultValue={this.props.lname} required placeholder="" />
                                </Form.Group>

                                <Form.Group controlId="Email">
                                        <Form.Label>Email</Form.Label>
                                        <Form.Control type="text" name="Firstname" defaultValue={this.props.email} required placeholder="" />
                                </Form.Group>

                                <Form.Group controlId="Course">
                                        <Form.Label>Course</Form.Label>
                                        <Form.Control type="text" name="Course" defaultValue={this.props.course} required placeholder="" />
                                </Form.Group>
                                <p></p>
                                <Form.Group>
                                    <Button variant="primary" type="submit">
                                        Update
                                    </Button>
                                </Form.Group>
                                </Form>
                            </Col>
                        </Row>
                    </Modal.Body>
                    <Modal.Footer>
                    <Button variant="danger" type="submit" onClick={this.props.onHide}>
                            Close
                    </Button>

                    </Modal.Footer>
                </Modal>
            </div>
        )
    }
}
